import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import useMediaQuery from "@mui/material/useMediaQuery";
import { makeStyles } from "@mui/styles";

const useCountryDetailsStyles = makeStyles({
  country_details: {
    margin: "15px auto 0",
    padding: "15px",
    maxWidth: 1400,
    minHeight: "100vh",
  },

  country_details_darkMode: {
    margin: "15px auto 0",
    padding: "15px",
    maxWidth: 1400,
    minHeight: "100vh",
  },
  back: {
    backgroundColor: "#fff",
    border: 0,
    outline: 0,
    boxShadow: "0px 3px 15px rgba(0,0,0,0.2)",
    width: "150px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "10px",
    padding: 10,
    fontWeight: "bold",
    cursor: "pointer",
  },
  back_darkMode: {
    backgroundColor: "#202c37",
    border: 0,
    outline: 0,
    boxShadow: "0px 3px 15px rgba(0,0,0,0.2)",
    width: "150px",
    display: "flex",
    color: "#fff",
    justifyContent: "center",
    alignItems: "center",
    gap: "10px",
    padding: 10,
    fontWeight: "bold",
    cursor: "pointer",
  },
  country_details_body: {
    margin: "15px auto 0",
    padding: "15px",
    maxWidth: 1400,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "40px",
  },

  country_details_small_body: {
    flexDirection: "column",
    margin: "15px auto 0",
    padding: "15px",
    maxWidth: 1400,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "40px",
  },

  img_container: {
    flex: 0.5,
  },
  img_container_images: {
    width: "100%",
    objectFit: "cover",
  },
  info: {
    flex: 0.5,
  },
  info_h2: {
    marginBottom: 15,
  },
  info_container: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "45px",
  },
  info_container_small: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    marginBottom: "45px",
  },
  info_values: {
    color: "#808080",
  },
  info_values_darkMode: {
    color: "#fff",
  },
  border_country: {
    padding: "5px 10px",
    backgroundColor: "#fff",
    display: "inline-block",
    margin: 10,
    cusror: "pointer",
    boxShadow: "0px 3px 15px rgba(0,0,0,0.2)",
  },
  border_country_darkMode: {
    backgroundColor: "#202c37",
    color: "#fff",
    padding: "5px 10px",
    display: "inline-block",
    margin: 10,
    cusror: "pointer",
    boxShadow: "0px 3px 15px rgba(0,0,0,0.2)",
  },
  left_info: {
    marginBottom: 20,
  },
  countrydetailsdivmode: {
    backgroundColor: "#2b3945",
    color: "#fff",
  },
});
export const CountryDetails = ({ mode }: any) => {
  const CountryDetailsClasses = useCountryDetailsStyles();
  const location = useLocation();
  const dataset: any = location.state;

  const matches = useMediaQuery("(max-width:800px)");

  console.log(dataset, "dataset");

  const navigates = useNavigate();
  const goBack = () => {
    navigates("/");
  };
  return (
    <div className={mode ? CountryDetailsClasses.countrydetailsdivmode : ""}>
      <div
        className={
          mode
            ? CountryDetailsClasses.country_details_darkMode
            : CountryDetailsClasses.country_details
        }
      >
        <button
          className={
            mode
              ? CountryDetailsClasses.back_darkMode
              : CountryDetailsClasses.back
          }
          onClick={goBack}
        >
          <ArrowBackIcon />
          <p>Back</p>
        </button>

        <div
          className={
            matches
              ? CountryDetailsClasses.country_details_small_body
              : CountryDetailsClasses.country_details_body
          }
        >
          <div className={CountryDetailsClasses.img_container}>
            <img
              src={dataset?.flags.svg}
              className={CountryDetailsClasses.img_container_images}
            />
          </div>

          <div className={CountryDetailsClasses.info}>
            <h2 className={CountryDetailsClasses.info_h2}>name</h2>
            <div
              className={
                matches
                  ? CountryDetailsClasses.info_container_small
                  : CountryDetailsClasses.info_container
              }
            >
              <div className={matches ? CountryDetailsClasses.left_info : ""}>
                <p>
                  Native Name:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    {" "}
                    {dataset?.name?.nativeName?.eng?.common}
                  </span>
                </p>
                <p>
                  Population:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    {dataset?.population}
                  </span>
                </p>
                <p>
                  Region:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    {dataset?.region}
                  </span>
                </p>
                <p>
                  Sub Region:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    {dataset?.subregion}
                  </span>
                </p>
              </div>
              <div className="right_info">
                <p>
                  Capital:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    Test
                  </span>
                </p>
                <p>
                  Top-level Domain:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    Test
                  </span>
                </p>
                <p>
                  Currencies:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    {dataset?.currencies?.DOP?.name}
                  </span>
                </p>
                <p>
                  Languages:
                  <span
                    className={
                      mode
                        ? CountryDetailsClasses.info_values_darkMode
                        : CountryDetailsClasses.info_values
                    }
                  >
                    {" "}
                    {dataset?.languages?.eng}
                  </span>
                </p>
              </div>
            </div>
            Border Countries :
            {dataset?.borders.map((data: string) => (
              <div
                className={
                  mode
                    ? CountryDetailsClasses.border_country_darkMode
                    : CountryDetailsClasses.border_country
                }
              >
                <p>{data}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};
