import React, { useRef, useState, useEffect } from "react";
import SearchIcon from "@mui/icons-material/Search";
import Country from "../component/Country/Index";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles({
  app_body: {
    margin: "15px auto 0",
    padding: "15px",
    maxWidth: "1400px",
  },
  inputs: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  search_inputs: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    boxShadow: "0px 3px 15px rgba(0,0,0,0.2)",
    width: "300px",
    padding: 5,
    borderRadius: "5px",
    gap: "5px",
  },
  search_inputs_input: {
    border: 0,
    backgroundColor: "transparent",
    outline: "none",
    fontSize: "18px",
  },
  select_region: {
    width: "300px",
    backgroundColor: "#fff",
    boxShadow: "0px 3px 15px rgba(0,0,0,0.2)",
    padding: 5,
    borderRadius: 5,
  },
  select_region_select: {
    padding: 5,
    width: "100%",
    backgroundColor: "transparent",
    border: "none",
    outline: "none",
  },
  countries: {
    maxWidth: 1400,
    padding: 15,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexWrap: "wrap",
    gap: 35,
    paddingLeft: 0,
  },
});

const HomePage = () => {
  const classes = useStyles();
  const countriesInputRef = useRef<any>();
  const regionRef = useRef<any>();
  const noCountries = countries.status
  const [countries, setCountries] = useState<any>();
  const fetchData = async () => {
    const response = await fetch("https://restcountries.com/v3.1/all");
    const data = await response.json();
    setCountries(data);
  };

  useEffect(() => {
    try {
      fetchData();
    } catch (error) {
      console.log(error);
    }
  }, []);

  const searchCountries = () => {
    const searchValue = countriesInputRef.current.value;
    if (searchValue.trim()) {
      const fetchSearch = async () => {
        const responseSearch = await fetch(
          `https://restcountries.com/v3.1/region/${searchValue}`
        );
        const data = await responseSearch.json();
        setCountries(data);
      };

      try {
        fetchSearch();
      } catch (error) {
        console.log(error);
      }
    } else {
      fetchData();
    }
  };

  return (
    <div className={classes.app_body}>
      <div className={classes.inputs}>
        <div className={classes.search_inputs}>
          <SearchIcon />
          <input
            type="text"
            placeholder="Search for a Country ..."
            className={classes.search_inputs_input}
            ref={countriesInputRef}
            onChange={searchCountries}
          />
        </div>
        <div className={classes.select_region}>
          <select className={classes.select_region_select} ref={regionRef}>
            <option>All</option>
            <option>Africa</option>
            <option>America</option>
            <option>Asia</option>
            <option>europe</option>
          </select>
        </div>
      </div>

      <div className={classes.countries}>
        {" "}
        <Country country={countries} />
      </div>
    </div>
  );
};

export default HomePage;
